# Convert datetime object to EST
def convertToEST(dateTime):
    if (constants['current_timezone'] != 'EDT' and
       constants['current_timezone'] != 'EST' and
       constants['current_timezone'] != 'Eastern Daylight Time'):
        # localize to current time zone
        currTimeZone = pytz.timezone(constants['current_timezone'])
        dateTime = currTimeZone.localize(dateTime)
        dateTime = dateTime.astimezone(constants['eastern_timezone'])
        dateTime = dateTime.replace(tzinfo=None)
    return dateTime


def findAllDays(date, upToDate):
    delta = datetime.timedelta(1)
    dates = []
    while (date < upToDate):
        dates.append(date)
        date += delta
    return dates

def isTradingDay(date):
    if date.strftime("%Y-%m-%d") in trading_days:
        return True
    return False

# Return list of valid trading days from date on
def findTradingDays(date, upToDate):
    delta = datetime.timedelta(1)
    dates = []
    while (date < upToDate):
        if (date.strftime("%Y-%m-%d") in trading_days):
            dates.append(date)
        date += delta

    return dates


# Find weight between 0-1 based on function
def findWeight(date, function):
    time = date
    functions = constants['functions']
    if (function not in functions):
        return 0

    # If on a weekday between Monday 4:00 and Friday 4:00
    currTime = datetime.datetime(date.year, date.month, date.day, 16)
    if (date > currTime):
        date -= datetime.timedelta(days=1)

    difference = currTime - date
    secondsInDay = 86400
    seconds = difference.seconds
    x = 1 - (seconds / secondsInDay)

    dayOfWeek = time.weekday()
    timeDiff = (time - datetime.datetime(time.year, time.month, time.day)).total_seconds()
    fourPM = 16 * 60 * 60
    # If it is after Friday 4pm and before Monday 4pm
    if ((dayOfWeek == 4 and timeDiff > fourPM) or
        (dayOfWeek == 5) or (dayOfWeek == 6) or
        (dayOfWeek == 0 and timeDiff < fourPM)):
        # Move currTime to 4 pm on Monday
        while (currTime.weekday() != 0):
            currTime += datetime.timedelta(days=1)

        difference = (currTime - time).total_seconds()
        secondsInDay *= 3
        x = 1 - (difference / secondsInDay)

    if (function == '1'):
        return 1
    elif (function == 'x'):
        return x
    elif (function == 'x^2'):
        return x * x
    elif (function == 'x^4'):
        return x * x * x * x
    elif (function == 'log(x)'):
        x = (x * 3) + 0.05
        y = math.log(x) + 3
        yMax = math.log(3.05) + 3
        return y / yMax
    else:
        return 0


# Return number of days since join date
def findJoinDate(dateString):
    dateTime = parse(dateString)
    currTime = convertToEST(datetime.datetime.now())
    days = (currTime - dateTime).days
    return days
